package response;

/**
 * Success response generated by services.
 */
public class SuccessResponse extends GenericResponse {

    /** Success message */
    private String message;

    /** Default constructor */
    public SuccessResponse() {

    }

    /** Main constructor */
    SuccessResponse(String message) {
        this.message = message;
    }

    // Getter
    public String getMessage() {
        return message;
    }
}
